import React, { useState } from "react";
import styles from "./Styles/Styles.module.css";
import { useTheme } from "@mui/material/styles";
import Box from "@mui/material/Box";
import MobileStepper from "@mui/material/MobileStepper";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import KeyboardArrowLeft from "@mui/icons-material/KeyboardArrowLeft";
import KeyboardArrowRight from "@mui/icons-material/KeyboardArrowRight";
import SwipeableViews from "react-swipeable-views-react-18-fix";
import { autoPlay } from "react-swipeable-views-utils";
import { Avatar, Card, CardContent } from "@mui/material";
import StarIcon from "@mui/icons-material/Star";

const reviews = [
  {
    companyLogo:
      "data:image/png;base64,",
    review:
      "I had the pleasure of experiencing the next generation of card solutions with this incredible product. It's refreshing to see such innovation in the financial industry.",
    userName: "Nick Babich",
    userRole: "Lead Designer",
    userImage:
      "data:image/jpeg;base64,",
    star: 4,
  },
  {
    companyLogo:
      "data:image/png;base64,",
    review:
      "I had the pleasure of experiencing the next generation of card solutions with this incredible product. It's refreshing to see such innovation in the financial industry.",
    userName: "David",
    userRole: "Lead Designer",
    userImage:
      "data:image/jpeg;base64,",
    star: 4,
  },
  {
    companyLogo:
      "data:image/png;base64,",
    review:
      "I had the pleasure of experiencing the next generation of card solutions with this incredible product. It's refreshing to see such innovation in the financial industry.",
    userName: "Arun Praveen",
    userRole: "Lead Designer",
    userImage:
      "data:image/jpeg;base64,",
    star: 4,
  },
  {
    companyLogo:
      "data:image/png;base64,",
    review:
      "I had the pleasure of experiencing the next generation of card solutions with this incredible product. It's refreshing to see such innovation in the financial industry.",
    userName: "Praveen",
    userRole: "Lead Designer",
    userImage:
      "data:image/jpeg;base64,",
    star: 4,
  },
];

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const Testimonial = () => {
  const theme = useTheme();
  const [activeStep, setActiveStep] = useState(0);
  const maxSteps = reviews.length;

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleStepChange = (step) => {
    setActiveStep(step);
  };

  const generateStars = (count) => {
    return Array.from({ length: count }, (_, index) => (
      <StarIcon key={index} sx={{ color: '#E3AD54', mr: 0.5 }} />
    ));
  };

  return (
    <div className={styles.Testimonial_Container}>
      <Box sx={{ maxWidth: 400, flexGrow: 1 }}>
      <Paper
        square
        elevation={0}
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: 50,
          pl: 2,
          bgcolor: "background.default",
        }}
      >
        <img
          src={reviews[activeStep].companyLogo}
          alt={reviews[activeStep].userName}
          style={{ width: "100px" }}
        />
      </Paper>
      <AutoPlaySwipeableViews
        axis={theme.direction === "rtl" ? "x-reverse" : "x"}
        index={activeStep}
        onChangeIndex={handleStepChange}
        enableMouseEvents
      >
        {reviews.map((review, index) => (
          <div key={index}>
            {Math.abs(activeStep - index) <= 2 ? (
              <Box
                sx={{
                  height: 255,
                  display: "block",
                  maxWidth: 400,
                  overflow: "hidden",
                  width: "100%",
                  
                }}
              >
                <Typography sx={{color:"#24282F",textAlign:"center",marginBottom:"20px"}}>{review.review}</Typography>
                <Box sx={{ display: "flex",gap:"4px",flexDirection:"column",justifyContent:"center", alignItems: "center", mt: 1 }}>
                  <Avatar src={review.userImage} alt={review.userName} />
                  <Box sx={{ marginLeft: 1 }}>
                    <Typography variant="subtitle1" sx={{textAlign:"center"}}>
                      {review.userName}
                    </Typography>
                    <Typography variant="subtitle2" sx={{textAlign:"center",color:"#5A6475"}}>
                      {review.userRole}
                    </Typography>
                    <Typography >
                    {generateStars(review.star)}
                    </Typography>
                  </Box>
                </Box>
              </Box>
            ) : null}
          </div>
        ))}
      </AutoPlaySwipeableViews>
      <MobileStepper
        steps={maxSteps}
        position="static"
        activeStep={activeStep}
        nextButton={
          <Button
            size="small"
            onClick={handleNext}
            disabled={activeStep === maxSteps - 1}
          >
            Next
            {theme.direction === "rtl" ? (
              <KeyboardArrowLeft />
            ) : (
              <KeyboardArrowRight />
            )}
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
            {theme.direction === "rtl" ? (
              <KeyboardArrowRight />
            ) : (
              <KeyboardArrowLeft />
            )}
            Back
          </Button>
        }
      />
    </Box>
    </div>
  );
};

export default Testimonial;
